(function($) {
  'use strict';

  $(function() {
    $('[data-toggle="sweet-alert"]').on('click', function(e) {
      e.preventDefault();

      const alertType = $(this).data('sweet-alert');

      switch (alertType) {
        case 'basic':
          Swal.fire({
            title: "Here's a message!",
            text: "A few words about this sweet alert ...",
            icon: 'info',
            confirmButtonText: 'OK',
            customClass: {
              confirmButton: 'btn btn-primary'
            },
            buttonsStyling: false
          });
          break;

        case 'info':
          Swal.fire({
            title: "Info",
            text: "This is an info alert.",
            icon: 'info',
            confirmButtonText: 'OK',
            customClass: {
              confirmButton: 'btn btn-info'
            },
            buttonsStyling: false
          });
          break;

        case 'success':
          Swal.fire({
            title: "Success",
            text: "Operation was successful!",
            icon: 'success',
            confirmButtonText: 'Great!',
            customClass: {
              confirmButton: 'btn btn-success'
            },
            buttonsStyling: false
          });
          break;

        case 'warning':
          Swal.fire({
            title: "Warning",
            text: "This is a warning alert.",
            icon: 'warning',
            confirmButtonText: 'Understood',
            customClass: {
              confirmButton: 'btn btn-warning'
            },
            buttonsStyling: false
          });
          break;

        case 'question':
          Swal.fire({
            title: "Are you sure?",
            text: "Think twice before continuing.",
            icon: 'question',
            showCancelButton: true,
            confirmButtonText: 'Yes, proceed',
            cancelButtonText: 'Cancel',
            customClass: {
              confirmButton: 'btn btn-default',
              cancelButton: 'btn btn-secondary'
            },
            buttonsStyling: false
          });
          break;

        case 'confirm':
          Swal.fire({
            title: "Are you sure?",
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'Cancel',
            customClass: {
              confirmButton: 'btn btn-danger',
              cancelButton: 'btn btn-secondary'
            },
            buttonsStyling: false
          }).then((result) => {
            if (result.isConfirmed) {
              Swal.fire({
                title: "Deleted!",
                text: "Your file has been deleted.",
                icon: 'success',
                confirmButtonText: 'OK',
                customClass: {
                  confirmButton: 'btn btn-primary'
                },
                buttonsStyling: false
              });
            }
          });
          break;

        case 'image':
          Swal.fire({
            title: "Sweet",
            text: "Modal with a custom image ...",
            imageUrl: "/static/img/ill/ill-1.svg", // sesuaikan path
            confirmButtonText: "Super!",
            customClass: {
              confirmButton: 'btn btn-primary'
            },
            buttonsStyling: false
          });
          break;

        case 'timer':
          Swal.fire({
            title: "Auto close alert!",
            text: "I will close in 2 seconds.",
            timer: 2000,
            showConfirmButton: false
          });
          break;

        default:
          console.warn('Unknown alert type:', alertType);
      }
    });
  });
})(jQuery);
